settings.setting:

YUI().use (
    'aui-node',
    function(F) {
        var child = F.one('input[id$="url-icon_facebook"]');
        var baseURL = F.Node.create('<div />');
        var parent = F.one('.control-group');
        parent.insertBefore(baseURL,child);
        baseURL.setStyles({"background": '#EEE', "border": '1px solid #CCC', "color": '#555', "height": '20px',  "line-height": '20px', "padding": '4px 6px', "width": '206px' });
        baseURL.setHTML('http://www.facebook.com');
    }
);


